package com.example.consumirapirest;

import android.content.Intent;
import android.graphics.Bitmap;
import android.os.Bundle;
import android.provider.MediaStore;
import android.support.v7.app.AppCompatActivity;
import android.util.Base64;
import android.util.Log;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;
import android.widget.ImageView;
import android.widget.Toast;

import com.android.volley.Request;
import com.android.volley.Response;
import com.android.volley.VolleyError;
import com.android.volley.toolbox.StringRequest;
import com.android.volley.toolbox.Volley;
import com.example.consumirapirest.Models.remote.ApiUtils;
import com.example.consumirapirest.interfaces.ImagenApi;

import java.io.ByteArrayOutputStream;
import java.util.HashMap;
import java.util.Map;

import retrofit2.Call;
import retrofit2.Callback;
import retrofit2.Retrofit;
import retrofit2.converter.gson.GsonConverterFactory;

public class MainActivity extends AppCompatActivity {
    EditText txtNombreImagen, txtCarpeta, txtOrigen;
    Button btnEnviar, btnAbrirCamara, btnCamera1, btnCamera2;
    ImageView imgv1, imgv2;
    String imgBase1, imgBase2;
    int xfoto;
    private ImagenApi imagenApi;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        txtNombreImagen = findViewById(R.id.txtNombre);
        txtCarpeta = findViewById(R.id.txtCarpeta);
        txtOrigen = findViewById(R.id.txtOrigen);
        btnEnviar = findViewById(R.id.btnEnviar);
        btnCamera1 = findViewById(R.id.btnCamara1);
        btnCamera2 = findViewById(R.id.btnCamara2);
        imgv1 = findViewById(R.id.imgv1);
        imgv2 = findViewById(R.id.imgv2);
        //OJO
        imagenApi = ApiUtils.getAPIService();
        //camara uno
        btnCamera1.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                abrirCamara();
                xfoto = 1;
            }
        });
        btnCamera2.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                abrirCamara();
                xfoto = 2;
            }
        });

        btnEnviar.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                String p1, p2, p3, p4;
                //p1 = imgBase1.toString();
                p1 = "";
                p2 = txtNombreImagen.getText().toString();
                p3 = txtCarpeta.getText().toString();
                p4 = txtOrigen.getText().toString();

                System.out.println(p1);
                System.out.println(p2);
                System.out.println(p3);
                System.out.println(p4);

                //subirImagen(p1,p2, p3, p4);
                subirImagenApi(p1,p2, p3, p4);
            }
        });
    }

    protected void onActivityResult(int requestCode, int resultCode, Intent data) {
        super.onActivityResult(requestCode, resultCode, data);
        if (requestCode == 1 && resultCode == RESULT_OK) {
            Bundle extras = data.getExtras();
            Bitmap imgBitmap = (Bitmap) extras.get("data");
            switch (xfoto){
                case 1:
                    imgv1.setImageBitmap(imgBitmap);
                    imgBase1 = getStringImage(imgBitmap);
                    break;
                case 2:
                    imgv2.setImageBitmap(imgBitmap);
                    imgBase2 = getStringImage(imgBitmap);
                    break;
            }

        }
    }

    private void abrirCamara(){
        Intent intent = new Intent(MediaStore.ACTION_IMAGE_CAPTURE);
        if(intent.resolveActivity(getPackageManager()) != null){
            startActivityForResult(intent, 1);
        }
    }

    private void subirImagenApi(String base64img, String nombreimg,  String carpeta,  String origen) {
        imagenApi.SubirImagenApi(base64img, nombreimg, carpeta, origen).enqueue(new Callback<String>() {
            @Override
            public void onResponse(Call<String> call, retrofit2.Response<String> response) {
                if(response.isSuccessful()) {
                    //showResponse(response.body().toString());
                    Log.i("Tag", "post submitted to API." + response.body().toString());
                }
            }

            @Override
            public void onFailure(Call<String> call, Throwable t) {
                Log.e("Error", call.toString());
            }
        });
    }

    private void subirImagen(final String base64img,final String nombreimg, final String carpeta, final String origen) {

        //String url = "https://jsonplaceholder.typicode.com/posts";
        String url = "http://192.168.2.114:8082/api/Imagen";

        StringRequest postResquest = new StringRequest(Request.Method.POST, url, new Response.Listener<String>() {
            @Override
            public void onResponse(String response) {
                Toast.makeText(MainActivity.this, "RESULTADO POST = " + response, Toast.LENGTH_LONG).show();
            }
        }, new Response.ErrorListener() {
            @Override
            public void onErrorResponse(VolleyError error) {
                Log.e("Error", error.getMessage());
            }
        }) {
            protected Map<String, String> getParams() {
                Map<String, String> params = new HashMap<>();
                params.put("imagenbase", base64img);
                params.put("nombreimagen", nombreimg);
                params.put("nombrecarpeta", carpeta);
                params.put("origen", origen);

                return params;
            }
        };
        Volley.newRequestQueue(this).add(postResquest);
    }

    public String getStringImage(Bitmap bmp){
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        bmp.compress(Bitmap.CompressFormat.JPEG, 100, baos);
        byte[] imageBytes = baos.toByteArray();
        String encodedImage = Base64.encodeToString(imageBytes, Base64.DEFAULT);
        String ImageBase64 = encodedImage.replace("\n","");
        return ImageBase64;

    }
}